async function 更新变量(变量数据) {
  try {
    await window.SillyTavern.getContext().eventSource.emit('在最新楼层更新变量', 变量数据);
    await Promise.all(Object.entries(变量数据).map(([key, value]) => setvar(key, value)));
    return true;
  } catch (error) {
    console.error('更新变量时出错:', error);
    return false;
  }
}

// --- 读取当前状态 ---
let 当前好感度 = parseInt(getvar('变量.林晴羽.好感度') || 0);
const 事件状态_出差 = parseInt(getvar('变量.林晴羽.事件状态.突然出差') || 0);
const 事件状态_直播 = parseInt(getvar('变量.林晴羽.事件状态.直播事故') || 0);
const 事件状态_定情 = parseInt(getvar('变量.林晴羽.事件状态.情定终身') || 0);
const 事件状态_约会 = parseInt(getvar('变量.林晴羽.事件状态.主动约会') || 0);
const 事件状态_求婚 = parseInt(getvar('变量.林晴羽.事件状态.主动求婚') || 0);

let 好感度被修正 = false;
// 在这里记录修正前的值
const 原始好感度_本轮开始时 = 当前好感度; 

// --- 好感度上限检查与修正 ---
if (事件状态_出差 < 1 && 当前好感度 > 399) {
  当前好感度 = 399;
  好感度被修正 = true;
}
if (事件状态_出差 >= 1 && 事件状态_直播 < 1 && 当前好感度 > 599) {
  当前好感度 = 599;
  好感度被修正 = true;
}
if (事件状态_直播 >= 1 && 事件状态_定情 < 1 && 当前好感度 > 799) {
  当前好感度 = 799;
  好感度被修正 = true;
}

// --- 执行修正操作 ---
if (好感度被修正) {
  await 更新变量({'变量.林晴羽.好感度': 当前好感度});
}

// --- 定义全局 config 对象 ---
const config = {
  角色名: '林晴羽',
  好感度: 当前好感度, // 使用修正后的值
  事件状态: {
    突然出差: 事件状态_出差,
    主动约会: 事件状态_约会,
    直播事故: 事件状态_直播,
    情定终身: 事件状态_定情,
    主动求婚: 事件状态_求婚,
  },
  // *** 关键修改点：将原始好感度也加入 config 对象 ***
  原始好感度_本轮开始时: 原始好感度_本轮开始时 
};

// --- 广播 config 对象 ---
define('config', config);
_%>
<!-- *** 关键修改点：通过 config 对象访问原始好感度 *** -->
<!-- Config Debug: 好感度=<%= config.好感度 %>, 出差=<%= config.事件状态.突然出差 %>, 直播=<%= config.事件状态.直播事故 %>, 定情=<%= config.事件状态.情定终身 %>, 修正前好感度=<%= config.原始好感度_本轮开始时 %> -->
<%_
// EJS 代码块结束（可以为空）
%>
